(111, 0, 'Home Get In Touch', '', '<div class="uk-text-center">\n  <h1 class="uk-heading-large uk-heading-large">Get in touch</h1>\n  <p class="uk-text-center uk-margin-top-remove">Our team will be glad to help you anytime with general<br /> or technical questions, suggestions or comments.</p>\n  <p class="uk-margin-top">\n    <a href="#" class="uk-icon-button uk-icon-facebook"></a>\n    <a href="#" class="uk-icon-button uk-icon-twitter"></a>\n    <a href="#" class="uk-icon-button uk-icon-google-plus"></a>\n  </p>\n</div>', 1, 'bottom-c', 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'mod_custom', 1, 0, '{"prepare_content":"0","backgroundimage":"","layout":"_:default","moduleclass_sfx":"","cache":"1","cache_time":"900","cachemode":"static","module_tag":"div","bootstrap_size":"0","header_tag":"h3","header_class":"","style":"0"}', 0, '*'),
